  static List<Dikr> khatmqoran = [
    Dikr(id: "1", name: "اللهم ارحمني بالقرءان واجعله لي إماما ونورا وهدى ورحمة \nاللهم ذكرني منه مانسيت وعلمني منه ماجهلت وارزقني تلاوته آناء الليل وأطراف النهار واجعله لي حجة يارب العالمين \nاللهم أصلح لي ديني الذي هو عصمة أمري، وأصلح لي دنياي التي فيها معاشي، وأصلح لي آخرتي التي فيها معادي، واجعل الحياة زيادة لي في كل خير واجعل الموت راحة لي من كل شر \n", tikrar: 1),
    Dikr(id: "1", name: "اللهم اجعل خير عمري آخره وخير عملي خواتمه وخير أيامي يوم ألقاك فيه \nاللهم إني أسألك عيشة هنية وميتة سوية ومردا غير مخز ولا فاضح \nاللهم إني أسألك خير المسألة وخير الدعاء وخير النجاح وخير العلم وخير العمل وخير الثواب وخير الحياة وخير الممات وثبتني وثقل موازيني وحقق إيماني وارفع درجتي وتقبل صلاتي واغفر خطيئاتي وأسألك العلا من الجنة \n", tikrar: 1),
    Dikr(id: "1", name: "اللهم إني أسألك موجبات رحمتك وعزائم مغفرتك والسلامة من كل إثم والغنيمة من كل بر والفوز بالجنة والنجاة من النار \nاللهم أحسن عاقبتنا في الأمور كلها، وأجرنا من خزي الدنيا وعذاب الآخرة \n", tikrar: 1),
    Dikr(id: "1", name: "اللهم اقسم لنا من خشيتك ماتحول به بيننا وبين معصيتك ومن طاعتك ماتبلغنا بها جنتك ومن اليقين ماتهون به علينا مصائب الدنيا ومتعنا بأسماعنا وأبصارنا وقوتنا ماأحييتنا واجعله الوارث منا واجعل ثأرنا على من ظلمنا وانصرنا على من عادانا ولا تجعل مصيبتنا في ديننا ولا تجعل الدنيا أكبر همنا ولا مبلغ علمنا ولا تسلط علينا من لا يرحمنا \n", tikrar: 1),
    Dikr(id: "1", name: "اللهم لا تدع لنا ذنبا إلا غفرته ولا هما إلا فرجته ولا دينا إلا قضيته ولا حاجة من حوائج الدنيا والآخرة إلا قضيتها ياأرحم الراحمين \n ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار وصلى الله على سيدنا ونبينا محمد وعلى آله وأصحابه الأخيار وسلم تسليما كثيرا.", tikrar: 1),
  ];
  static List<Dikr> douaemoutawafa = [
    Dikr(id: "1", name: "اللهم أبدله دارا خيرا من داره، وأهلا خيرا من أهله، وأدخله الجنة، وأعذه من عذاب القبر ومن عذاب النار. \nاللهم عامله بما أنت أهله، ولا تعامله بما هو أهله. \nاللهم  اجزه عن الإحسان إحسانا وعن الإساءة عفوا وغفرانا. \nاللهم  إن كان محسنا فزد من حسناته، وإن كان مسيئا فتجاوز عن سيئاته.", tikrar: 1),
    Dikr(id: "1", name: "اللهم أدخله الجنة من غير مناقشة حساب ولا سابقة عذاب. \nاللهم آنسه في وحدته وفي وحشته وفي غربته. \nاللهم  أنزله منزلا مباركا وأنت خير المنزلين. \nاللهم أنزله منازل الصديقين والشهداء والصالحين، وحسن أولئك رفيقا. \nاللهم اجعل قبره روضة من رياض الجنة، ولا تجعله حفرة من حفر النار.", tikrar: 1),
    Dikr(id: "1", name: "اللهم افسح له في قبره مد بصره، وافرش قبره من فراش الجنة. \nاللهم أعذه من عذاب القبر، وجفاف الأرض عن جنبيها. \nاللهم املأ قبره بالرضا والنور والفسحة والسرور. \nاللهم إنه في ذمتك وحبل جوارك، فقه فتنة القبر، وعذاب النار، وأنت أهل الوفاء والحق، فاغفر له وارحمه إنك أنت الغفور الرحيم.", tikrar: 1),
    Dikr(id: "1", name: "اللهم إنه عبدك وابن عبدك خرج من الدنيا وسعتها ومحبوبها وأحبائه فيها إلى ظلمة القبر وما هو لاقيه. \nاللهم إنه كان يشهد أنك لا إله إلا أنت وأن محمدا عبدك ورسولك وأنت أعلم به. \nاللهم إنا نتوسل بك إليك، ونقسم بك عليك أن ترحمه ولا تعذبه، وأن تثبته عند السؤال.", tikrar: 1),
    Dikr(id: "1", name: "اللهم إنه نزل بك وأنت خير منزول به، وأصبح فقيرا إلى رحمتك وأنت غني عن عذابه. \nاللهم آته برحمتك ورضاك، وقه فتنة القبر وعذابه، وآته برحمتك الأمن من عذابك حتى تبعثه إلى جنتك يا أرحم الراحمين. \nاللهم انقله من مواطن الدود وضيق اللحود إلى جنات الخلود. \nاللهم احمه تحت الأرض، واستره يوم العرض، ولا تخزه يوم يبعثون يوم لا ينفع مال ولا بنون إلا من أتى الله بقلب سليم.", tikrar: 1),
    Dikr(id: "1", name: "اللهم يمن كتابه، ويسر حسابه، وثقل بالحسنات ميزانه، وثبت على الصراط أقدامه، وأسكنه في أعلى الجنات بجوار حبيبك ومصطفاك (صلى الله عليه وسلم). \nاللهم أمنه من فزع يوم القيامة، ومن هول يوم القيامة، واجعل نفسه آمنة مطمئنة، ولقنه حجته. \nاللهم اجعله في بطن القبر مطمئنا وعند قيام الأشهاد آمن، وبجود رضوانك واثق، وإلى أعلى درجاتك سابق.", tikrar: 1),
    Dikr(id: "1", name: 'اللهم اجعل عن يمينه نورا حتى تبعثه آمنا مطمئنا في نور من نورك. \nاللهم انظر إليه نظرة رضا، فإن من تنظر إليه نظرة رضا لا تعذبه أبدا. \nاللهم أسكنه فسيح الجنان، واغفر له يا رحمن، وارحمه يا رحيم، وتجاوز عما تعلم يا عليم. \nاللهم اعف عنه فإنك القائل "ويعفو عن كثير". \nاللهم إنه جاء ببابك، وأناخ بجنابك، فجد عليه بعفوك وإكرامك وجود إحسانك.', tikrar: 1),
    Dikr(id: "1", name: 'اللهم إن رحمتك وسعت كل شيء فارحمه رحمة تطمئن بها نفسه، وتقر بها عينه. \nاللهم احشره مع المتقين إلى الرحمن وفدا. \nاللهم احشره مع أصحاب اليمين، واجعل تحيته سلام لك من أصحاب اليمين. \nاللهم بشره بقولك "كلوا واشربوا هنيئا بما أسلفتم في الأيام الخالية". \nاللهم اجعله من الذين سعدوا في الجنة خالدين فيها ما دامت السموات والأرض. \nاللهم لا نزكيه عليك، ولكنا نحسبه أنه \n أمن وعمل صالحا، فاجعل له جنتين ذواتي أفنان بحق قولك: "ولمن خاف مقام ربه جنتان".', tikrar: 1),
    Dikr(id: "1", name: 'اللهم شفع فيه نبينا ومصطفاك، واحشره تحت لوائه، واسقه من يده الشريفة شربة هنيئة لا يظمأ بعدها أبدا. \nاللهم اجعله في جنة الخلد "التي وعد المتقون كانت لهم جزاء ومصيرا. لهم فيها ما يشاؤون خالدين كان على ربك وعدا مسؤولا". \nاللهم إنه صبر على البلاء فلم يجزع، فامنحه درجة الصابرين الذين يوفون أجورهم بغير حساب فإنك القائل "إنما يوفي الصابرون أجرهم بغير حساب". \nاللهم إنه كان مصل لك، فثبته على الصراط يوم تزل الأقدام.', tikrar: 1),
    Dikr(id: "1", name: "اللهم إنه كان صائما لك، فأدخله الجنة من باب الريان. \nاللهم إنه كان لكتابك تال وسامع، فشفع فيه القرآن، وارحمه من النيران، واجعله يا رحمن يرتقي في الجنة إلى آخر آية قرأها أو سمعها، وآخر حرف تلاه. \nاللهم ارزقه بكل حرف في القرآن حلاوة، وبكل كلمة كرامة، وبكل اية سعادة، وبكل سورة سلامة، وبكل جزء جزاء. \nاللهم ارحمه فإنه كان مسلما، واغفر له فإنه كان مؤمنا، وأدخله الجنة فإنه كان بنبيك مصدقا، وسامحه فإنه كان لكتابك مرتلا.", tikrar: 1),
    Dikr(id: "1", name: "اللهم اغفر لحينا وميتنا، وشاهدنا وغائبنا، وصغيرنا وكبيرنا، وذكرنا وأنثانا. \nاللهم من أحييته منا فأحيه على الإسلام، ومن توفيته منا فتوفه على الإيمان. \nاللهم لا تحرمنا أجره ولا تضللنا بعده. \nاللهم ارحمنا إذا أتانا اليقين، وعرق منا الجبين، وكثر الأنين والحنين. \nاللهم ارحمنا إذا يئس منا الطبيب، وبكى علينا الحبيب، وتخلى عنا القريب والغريب، وارتفع النشيج والنحيب.", tikrar: 1),
    Dikr(id: "1", name: "اللهم ارحمنا إذا اشتدت الكربات، وتوالت الحسرات، وأطبقت الروعات، وفاضت العبرات، وتكشفت العورات، وتعطلت القوى والقدرات. \nاللهم ارحمنا إذا حملنا على الأعناق، وبلغت التراق، وقيل من راق وظن أنه \n الفراق والتفت الساق بالساق، إليك يا ربنا يومئذ المساق. \nاللهم ارحمنا إذا ورينا التراب، وغلقت القبور والأبواب، وانفض الأهل والأحباب، فإذا الوحشة والوحدة وهول الحساب. \nاللهم ارحمنا إذا فارقنا النعيم، وانقطع النسيم، وقيل ما غرك بربك الكريم.", tikrar: 1),
    Dikr(id: "1", name: "اللهم ارحمنا إذا أقمنا للسؤال، وخاننا المقال، ولم ينفع جاه ولامال ولا عيال، وقد حال الحال، وليس إلا فضل الكبير المتعال. \nاللهم ارحمنا إذا نسي اسمنا، ودرس رسمنا، وأحاط بنا قسمنا ووسعنا. \nاللهم ارحمنا إذا أهملنا فلم يزرنا زائر، ولم يذكرنا ذاكر، وما لنا من قوة ولا ناصر، فلا أمل إلا في القاهر القادر الغافر، يا من إذا وعد أوفى، وإذا توعد عفا، وشفع يا رب فينا حبيبنا المصطفى، واجعلنا ممن صفا ووفا، وبالله اكتفى، يا أرحم الراحمين، يا حي يا قيوم، يا بديع السموات والأرض، يا ذا الجلال والإكرام.", tikrar: 1),
    Dikr(id: "1", name: "اللهم إنه عبدك وابن عبدك وابن أمتك مات وهو يشهد لك بالوحدانية ولرسولك بالشهادة فاغفر له إنك أنت الغفار. \nاللهم لا تحرمنا أجره، ولا تفتنا بعده، واغفر لنا وله، واجمعنا معه في جنات النعيم يا رب العالمين. \nاللهم أنزل على أهله الصبر والسلوان وارضهم بقضائك. \nاللهم ثبتنا على القول الثابت في الحياة الدنيا، وفي الآخرة، ويوم يقوم الأشهاد. \nاللهم صل وسلم وبارك على سيدنا محمد، وعلى اله وصحبه وسلم إلى يوم الدين.", tikrar: 1),
  ];
 
